import {Component, ElementRef, OnInit} from '@angular/core';
import {Router} from "@angular/router";
import {TokenStorageService} from "../../Services/Security/token-storage.service";
import {FormControlerService} from "../../Services/form-controles.service";
import {AuthServerService} from "../../Services/Security/auth-server.service";
import {Login} from "../../Models/login";
import {FormControl, FormGroup} from "@angular/forms";
import {async} from "rxjs";

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  role= this.tokenStorage.getRole();
  login :Login = new Login();
  showsuccessmessage: boolean=false;
  showerrormessage: boolean=false;
  constructor(public formService :FormControlerService,
              private router:Router,
              private authService :AuthServerService,
              private tokenStorage :TokenStorageService) { }

  ngOnInit(): void {
    this.showsuccessmessage = false;
    this.showerrormessage = false;
  }
  validateAllFormFields(formGroup: FormGroup) {
    Object.keys(formGroup.controls).forEach(field => {
      const control = formGroup.get(field);
      if (control instanceof FormControl) {
        control.markAsTouched({ onlySelf: true });
      } else if (control instanceof FormGroup) {
        this.validateAllFormFields(control);
      }
    } );
  }
  async onSubmit() {

    if (this.formService.formGroupLogin.valid) {
      this.login.username = this.formService.formGroupLogin.value.username;
      this.login.password = this.formService.formGroupLogin.value.password;
      console.log('login :', this.login);

      this.authService.login(this.login).subscribe(
        async data => {
          console.log('data :', data);
          this.showerrormessage = false;
          this.showsuccessmessage = true;
          this.tokenStorage.savedata(data);

          if (this.role == "user") {
            console.log('role user:', this.role);
            await this.router.navigateByUrl('homeUser/DashbordUser');

          } else if (this.role == "admin") {
            console.log('role admin:', this.role);
            await this.router.navigateByUrl('homeAdmin/DashbordAdmin');

          }

          this.onClear();
        }
    , error => {
          console.log('error :',error);
          this.showerrormessage = true;
          this.showsuccessmessage = false;
          setTimeout(() => {
            this.showerrormessage = false,2000;
          });
        }
      );
    } else {
      this.validateAllFormFields(this.formService.formGroupLogin);
    }
  }

  onClear() {
    this.formService.formGroupLogin.reset();
  }

}
<body class=" ">
<div class="wrapper">
  <section class="login-content">
    <div class="container">
      <div class="row align-items-center justify-content-center height-self-center">
        <div class="col-lg-8">
          <div class="card auth-card">
            <div class="card-body p-0">
              <div class="d-flex align-items-center auth-content">
                <div class="col-lg-6 bg-primary content-left">
                  <div class="p-3">
                    <h2 class="mb-2 text-white ">Sign In</h2>
                    <form [formGroup]="formService.formGroupLogin">
                      <div class="row">
                        <div class="col-lg-12">
                          <div class="floating-label form-group">
                            <input
                              formControlName="username"
                              [ngClass]="{ 'is-invalid':formService.username && formService.username.errors && formService.username.touched}"
                              class="floating-input form-control" type="email" placeholder=" ">
                            <label>Username</label>
                            <div class="invalid-feedback" *ngIf="formService.username && formService.username.hasError('required')">
                              Please enter username*
                            </div>
                          </div>
                        </div>
                        <div class="col-lg-12">
                          <div class="floating-label form-group">
                            <input
                              formControlName="password"
                              [ngClass]="{ 'is-invalid': formService.password && formService.password.errors && formService.password.touched}"
                              class="floating-input form-control" type="password" placeholder=" "/>
                            <div class="invalid-feedback"
                                 *ngIf="formService.password && formService.password.hasError('required')" >
                              Please enter your password*
                            </div>
                            <label>Password</label>
                          </div>
                        </div>
                      </div>
                      <button type="submit" class="btn btn-white col-lg-12" (click)="onSubmit()">Sign In</button>
                      <p class="mt-3">
                        Create an Account <a href="auth-sign-up.html" class="text-white text-underline">Sign Up</a>
                      </p>
                    </form>
                  </div>
                </div>
                <div class="col-lg-6 content-right">
                  <img src="assets/images/login/01.png" class="img-fluid image-right" alt="">
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>
</div>
</body>

<!-- Alert container -->
<div class="alert-container">
  <!-- Alert success -->
  <div class="alert alert-success" role="alert" *ngIf="showsuccessmessage">Login successful! Redirecting...</div>

  <!-- Alert error -->
  <div class="alert alert-danger" role="alert" *ngIf="showerrormessage">Login failed. Please check your credentials and try again.</div>
</div>
